mapper.select:

SELECT  BoardCateName   AS strBoardCateName
FROM    TBoardCategory  
WHERE   BoardCateName = #{boardName}

SELECT  BoardCateName
FROM    TBoardCategory
WHERE   BoardCateName = #{boardName}

mapper.update:
