               VALUES(#{intCashNo}, #{intUserNo}, #{amount}, #{intCashAmt}, #{strPurchaseState}
                     ,#{tid}, #{code}, #{message}, #{user_id}, #{cid}, #{intorderNo}
                     ,#{pgcode}, #{transaction_date}, #{card_info}, #{payhash})

mapper.select:
SELECT   UserNo   
FROM TUser
WHERE UserId = #{id}

